@push('script')
<script>
    (function ($) {
        "use strict";

        $('.item_shift_icon').on('click', function() {
            const data = $(this).data();
            $(this).css('display', 'none');
            const itemtags = "item_shift_icon_replace" + data.auctionid;
            const origintags = "item_shift_icon" + data.auctionid;
            $('.' + itemtags).css('display', 'flex');
            const blocktags = "item-block-tags" + data.auctionid;
            const imagetags = "item-image-tags" + data.auctionid;
            $('.' + blocktags).css("display", 'flex');
            $('.' + imagetags).css('display', 'none');

            setTimeout(function() {
                $('.' + origintags).css('display', 'flex');
                $('.' + itemtags).css('display', 'none');
                $('.' + blocktags).css("display", 'none');
                $('.' + imagetags).css('display', 'flex');
            }, 7000);
        });

        $('.item_shift_icon_replace').on('click', function() {
            const data = $(this).data();
            $(this).css('display', 'none');
            const itemtags = "item_shift_icon" + data.auctionid;
            $('.' + itemtags).css('display', 'flex');
            const blocktags = "item-block-tags" + data.auctionid;
            const imagetags = "item-image-tags" + data.auctionid;
            $('.' + blocktags).css("display", 'none');
            $('.' + imagetags).css('display', 'flex');
        });
        
        var authflag = "{{ auth()->check() ? 'true' : 'false' }}";
        
        $(".attachiconpaper").on('click', function() {
            if(authflag == "false") {
                iziToast['warning']({
                    message: "@lang('You must logged in.')",
                    position: "topRight"
                });
            } else {
                var specimgmodal = $('#specModal');
                specimgmodal.modal('show');
                specimgmodal.find(".specmodal_image").attr('src', "https://7-bids.com/assets/images/product/" + $(this).data('imgsrc'));
                specimgmodal.find(".specmodaltitles").html($(this).data('imgsrc'));
            }
        });
        
        $('.auction__slider-btn').on('click', function() {
            const data = $(this).data();
            $('.item-slider-replace-image-view' + data.auctionid).attr("src", data.imagesrc);
        });
    })(jQuery);
</script>
